"use client";

import React, { useEffect, useState, useRef } from "react";
import { Link, useRouter, usePathname } from "@/components/index";
import { FiMenu, FiUser, FiLogOut } from "react-icons/fi";
import MobileSidebar from "./MobileSidebar";
import { ChevronDown, ChevronUp } from "lucide-react";

export const Header = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);
  const [profileOpen, setProfileOpen] = useState(false);
  const dropdownRef = useRef<HTMLLIElement>(null);

  const router = useRouter();
  const pathname = usePathname();

  useEffect(() => {
    const checkLogin = () => {
      const token =
        localStorage.getItem("token") || sessionStorage.getItem("token");
      setIsLoggedIn(!!token);
    };

    checkLogin();
    window.addEventListener("authChanged", checkLogin);

    return () => {
      window.removeEventListener("authChanged", checkLogin);
    };
  }, [pathname]);

  useEffect(() => {
    const handleClickOutside = (e: MouseEvent) => {
      if (
        dropdownRef.current &&
        !dropdownRef.current.contains(e.target as Node)
      ) {
        setProfileOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    sessionStorage.removeItem("token");
    window.dispatchEvent(new Event("authChanged"));
    router.push("/login");
  };

  return (
    <header className="text-black bg-white h-[90px] fixed w-full font-poppins z-50 border-b border-[#68686833] shadow-sm">
      <div className="container mx-auto flex justify-between items-center h-full px-4">
        {/* Logo */}
        <Link href="/home">
          <div className="items-center justify-center">
            <svg
              width="75"
              height="60"
              viewBox="0 0 84 66"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
              xmlnsXlink="http://www.w3.org/1999/xlink"
            >
              <rect width="84" height="66" fill="url(#pattern0)" />
              <defs>
                <pattern
                  id="pattern0"
                  patternContentUnits="objectBoundingBox"
                  width="1"
                  height="1"
                >
                  <use
                    xlinkHref="#image0"
                    transform="matrix(0.00373134 0 0 0.0047619 -0.61194 -0.442857)"
                  />
                </pattern>
                <image
                  id="image0"
                  width="612"
                  height="408"
                  preserveAspectRatio="none"
                  xlinkHref="data:image/png;base64,"
                />
              </defs>
            </svg>
          </div>
        </Link>

        {/* Desktop Nav */}
        {isLoggedIn ? (
          <ul className="hidden lg:flex gap-18 font-semibold text-[18px] items-center">
            <li
              className={`cursor-pointer ${
                pathname === "/programs"
                  ? "text-[#74BF44]"
                  : "text-black hover:text-[#74BF44]"
              }`}
              onClick={() => router.push("/programs")}
            >
              Home
            </li>
            <li
              className={`cursor-pointer ${
                pathname === "/extra-resources"
                  ? "text-[#74BF44]"
                  : "text-black hover:text-[#74BF44]"
              }`}
              onClick={() => router.push("/extra-resources")}
            >
              Extra Learning
            </li>
            <li
              className={`cursor-pointer ${
                pathname === "/contributions"
                  ? "text-[#74BF44]"
                  : "text-black hover:text-[#74BF44]"
              }`}
              onClick={() => router.push("/contributions")}
            >
              Submit Resources
            </li>

            {/* Profile Dropdown */}
            <li className="relative" ref={dropdownRef}>
              <button
                onClick={() => setProfileOpen(!profileOpen)}
                className={`flex items-center gap-2 cursor-pointer ${
                  pathname === "/Profile"
                    ? "text-[#74BF44]"
                    : "text-black hover:text-[#74BF44]"
                }`}
              >
                Profile
                {profileOpen ? (
                  <ChevronUp size={16} className="text-[#74BF44]" />
                ) : (
                  <ChevronDown size={16} className="text-gray-500" />
                )}
              </button>

              {profileOpen && (
                <div className="absolute right-[-80px] mt-3 w-65 h-43 bg-white rounded-[8px] shadow-lg border border-[#74BF44]/30 z-50 backdrop-blur-sm overflow-hidden transition-all duration-200">
                  {/* Fetch student + faculty data from localStorage */}
                  {(() => {
                    const email =
                      typeof window !== "undefined"
                        ? localStorage.getItem("email") ||
                          sessionStorage.getItem("email")
                        : null;
                    const name = email ? email.split("@")[0] : "User";

                    const faculty =
                      typeof window !== "undefined"
                        ? localStorage.getItem("facultyName") ||
                          sessionStorage.getItem("facultyName")
                        : null;

                    return (
                      <div className="px-5 py-3 bg-[#74BF44]/10 flex items-center gap-3 border-b border-[#74BF44]/20">
                        <div className="w-9 h-9 rounded-full bg-[#74BF44] flex items-center justify-center text-white font-semibold shrink-0">
                          {name.charAt(0).toUpperCase()}
                        </div>

                        <div className="min-w-0 flex-1">
                          <p
                            className="text-[15px] font-semibold text-gray-800 truncate max-w-[130px]"
                            title={name}
                          >
                            {name.charAt(0).toUpperCase() + name.slice(1)}
                          </p>
                          <p
                            className="text-sm text-gray-500 truncate max-w-[130px]"
                            title={faculty || "Faculty not set"}
                          >
                            {faculty || "Unknown Faculty"}
                          </p>
                        </div>
                      </div>
                    );
                  })()}

                  {/* Dashboard */}
                  <div
                    onClick={() => {
                      router.push("/profile");
                      setProfileOpen(false);
                    }}
                    className="flex items-center gap-3 px-5 py-3 cursor-pointer hover:bg-[#74BF44]/10 transition text-[15px] font-medium text-gray-800"
                  >
                    <FiUser className="text-[#74BF44] text-lg" />
                    <span>Dashboard</span>
                  </div>

                  {/* Divider */}
                  <div className="border-t border-gray-200"></div>

                  {/* Logout */}
                  <div
                    onClick={handleLogout}
                    className="flex items-center gap-3 px-5 py-3 cursor-pointer hover:bg-red-50 transition text-[15px] font-medium text-red-600"
                  >
                    <FiLogOut className="text-red-600 text-lg" />
                    <span>Logout</span>
                  </div>
                </div>
              )}
            </li>
          </ul>
        ) : (
          <Link href="/login" className="hidden lg:block">
            <button className="bg-[#74BF44] text-white w-[115px] h-[40px] rounded-[7px] cursor-pointer text-[16px] font-semibold hover:bg-white hover:text-[#74BF44] border transition">
              Login
            </button>
          </Link>
        )}

        {/* Mobile Menu Button */}
        <button
          className="lg:hidden text-3xl focus:outline-none"
          onClick={() => setMenuOpen(!menuOpen)}
        >
          <FiMenu className="text-black" />
        </button>
      </div>
      <MobileSidebar
        isLoggedIn={isLoggedIn}
        handleLogout={handleLogout}
        menuOpen={menuOpen}
        setMenuOpen={setMenuOpen}
      />
    </header>
  );
};
